mapper.select:
select count(*) from TB_post_${boardCode}



			and title


			and content


			and writer

		like '%${srchCon}%'


		and writer = #{memberId}

select count(*) from TB_report where
report_board = #{boardCode}
and report_post_no = #{postNo}
and report_reply_no = #{replyNo}
and reporter_id = #{reporterId}
and report_conf = 'N'
